import { useState, useEffect } from "react";
import axios from 'axios';

const useForm = (callback, validate) => {
  const [values, setValues] = useState({});
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      callback();
    }
  }, [errors]);

  const handleSubmit = (event) => {
    const data = {
      username: values.email,
      password: values.password
      };
      axios.post('http://ec2-43-204-240-96.ap-south-1.compute.amazonaws.com/api/auth/login', data)
        .then(response => {
          console.log(response);
          var log = response.headers.get('joka_auth_token');
          console.log(log)
          console.log(response.headers)
        });
      // fetch('http://ec2-43-204-240-96.ap-south-1.compute.amazonaws.com/api/auth/login', {
      //   method: 'POST', // or 'PUT'
      //   headers: {
      //     'Content-Type': 'application/json',
      //   },
      //   body: JSON.stringify(data),
      // })
      //   .then((response) => {
      //     console.log(response);
      //     var log = response.headers.get('joka_auth_token');
      //     console.log(log)
      //     console.log(response.headers)
      //   })
      //   .then((data) => {
      //     console.log('Success:', data);
      //   })
      //   .catch((error) => {
      //     console.error('Error:', error);
      //   });

    if (event) event.preventDefault();
    setErrors(validate(values));
    setIsSubmitting(true);
  };

  const handleChange = (event) => {
    event.persist();
    setValues((values) => ({
      ...values,
      [event.target.name]: event.target.value
    }));
  };

  return {
    handleChange,
    handleSubmit,
    values,
    errors
  };
};

export default useForm;
